epubCss:
body { font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif; line-height: 1.6; color: #111; }
h1 { color: #000; text-align: center; margin-bottom: 1em; font-weight: 700; text-transform: uppercase; letter-spacing: 1px; }
h2 { color: #000; margin-top: 2em; border-bottom: 2px solid #000; padding-bottom: 0.5em; font-weight: 600; }
p { margin-bottom: 1.2rem; }
blockquote { 
    font-style: normal; 
    border-left: 4px solid #00ced1; /* Electric Cyan-ish */
    padding-left: 15px; 
    background: #f0faff; 
    padding: 15px; 
    margin: 1.5em 0; 
    font-weight: 500;
} 
/* Specific styling for the Intro Images if needed */
img { max-width: 100%; display: block; margin: 2rem auto; border-radius: 4px; }
strong { color: #000; font-weight: 700; }

pdfCss:
@import url('https://fonts.googleapis.com/css2?family=Merriweather:ital,wght@0,300;0,400;0,700;1,400&family=Montserrat:wght@400;700&display=swap');

/* Default page margins for text content */
@page {
    size: A5;
    margin: 20mm 15mm;
}

/* Cover Page: Zero margins using :first pseudo-class */
@page :first {
    margin: 0mm;
}

body {
    font-family: 'Merriweather', serif;
    font-size: 9pt; /* Reduced by 1pt */
    line-height: 1.5;
    color: #222;
    text-align: justify;
    -webkit-font-smoothing: antialiased;
    margin: 0;
    padding: 0;
}

.cover-page {
    width: 148mm;
    height: 210mm;
    position: relative;
    margin: 0;
    padding: 0;
    page-break-after: always;
    overflow: hidden;
    display: block;
}

.chapter {
    page-break-after: always;
    width: 100%;
    display: block;
}

/* Headers */
h1, h2, h3 {
    font-family: 'Montserrat', sans-serif;
    color: #000;
    page-break-after: avoid;
}

h1 {
    font-size: 16pt;
    text-transform: uppercase;
    letter-spacing: 1px;
    text-align: center;
    margin-top: 2rem;
    margin-bottom: 2rem;
    page-break-before: always;
}

h2 {
    font-size: 13pt;
    margin-top: 1.5rem;
    border-bottom: 1px solid #ddd;
    padding-bottom: 0.2rem;
}

p {
    margin-bottom: 0.8em;
    orphans: 3;
    widows: 3;
}


/* Default Blockquote (Blue - Truth) */
blockquote {
    font-style: italic;
    margin: 1.5em 1em;
    padding: 1em 1.5em;
    background-color: #eff6ff; /* Cool Light Blue */
    border-left: 5px solid #3b82f6; /* Electric Blue */
    border-radius: 4px; 
    color: #1e3a8a; /* Dark Navy Text */
    box-shadow: 2px 2px 5px rgba(0,0,0,0.05);
    position: relative;
    font-size: 0.95em; 
}

/* Rule Blockquote (Purple - Authority/Aura) */
blockquote.rule-block {
    background-color: #f3e8ff; /* Light Purple */
    border-left: 5px solid #a855f7; /* Purple */
    color: #581c87; /* Dark Purple */
}

/* Challenge Blockquote (Orange - Action) */
blockquote.challenge-block {
    background-color: #fff7ed; /* Light Orange */
    border-left: 5px solid #f97316; /* Bright Orange */
    color: #7c2d12; /* Dark Brown/Red Text */
}

.quote-icon {
    float: left;
    width: 48px;  /* Larger size */
    height: 48px;
    margin-right: 15px;
    margin-top: 0px;
    opacity: 0.9;
    display: inline-block;
    vertical-align: middle;
    object-fit: contain;
    border: none;
    margin-bottom: 0;
    margin-left: 0;
    mix-blend-mode: multiply; /* Blends white background into transparency */
    filter: grayscale(100%) contrast(1.2); /* Sharpen and ensure B&W */
}

img {
    max-width: 100%;
    height: auto;
    display: block;
    margin: 2rem auto;
}

ul, ol {
    margin-left: 1.5em;
    margin-bottom: 1em;
}
